let english = []; let eng_change;
let spanish = []; let spa_change;
let scoreSpanish = []; let scoreEnglish = [];
let html1 = ""; let html2 = "";
let displayContent = null;
let spaLength = 0; let engLength = 0;
let goodSp = 0; let lbadSp = 0;
