import React, {useState} from 'react';

//import { showModalClick,setClickLanguage,setClickLettler } from './Letras.js';

const buildColor = (color) => {
    return {
        color 
    }
}



function LettlerContent(props) {
    const showModalClick = () => {
        {props.showModalClick()}
    }
    
    const html1 = props.spanish.map((spanish1,index) =>
            <img style={buildColor(props.color)} className={props.color} width="50" height="50" alt={spanish1.alt} src={spanish1.ruta} id={spanish1.id+'-'+index} name="original_sp" onClick={() => {showModalClick(); props.setClickLettler(spanish1.id+'-'+index); props.setClickLanguage("sp")}}></img>
        );

       

    const html2 = props.english.map((english1,index) =>
            <img style={buildColor(props.color)} className={props.color} width="50" height="50" alt={english1.alt} src={english1.ruta} id={english1.id+'-'+index} name="original_eng" disabled={english1.nombre === "space"?"disabled":''} onClick={() => {props.showModalClick(); props.setClickLettler(english1.id+'-'+index); props.setClickLanguage("eng")}}></img>
        );
        
    return (
        <>
            <div className="lettler_spanish">
            {html1}
            </div>
            <div className="lettler_english">
            {html2}
            </div>
        </>
    )
}

export default LettlerContent
